package com.evergent.corejava.logicalprogramming;

public class ArrayDemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int ar[]=new int[5];
		for(int i=0;i<ar.length;i++)
		{
			ar[i]=i;
		}
		for(int i=0;i<ar.length;i++)
		{
			System.out.println(ar[i]);
		}
	}

}
